 document.addEventListener('DOMContentLoaded',function(){'use strict';const contactForm=document.getElementById('contact-form');if(contactForm){contactForm.addEventListener('submit',validateForm);const formFields=contactForm.querySelectorAll('input,textarea,select');formFields.forEach(field=>{field.addEventListener('blur',function(){validateField(this);});field.addEventListener('input',function(){if(this.classList.contains('is-invalid')){validateField(this);}});});}function validateForm(event){event.preventDefault();resetFormErrors();let isValid=true;const name=document.getElementById('name');const email=document.getElementById('email');const phone=document.getElementById('phone');const subject=document.getElementById('subject');const message=document.getElementById('message');if(!name.value.trim()){showError(name,'name-error','Please enter your name');isValid=false;}else if(name.value.trim().length<2){showError(name,'name-error','Name must be at least 2 characters');isValid=false;}if(!email.value.trim()){showError(email,'email-error','Please enter your email');isValid=false;}else if(!isValidEmail(email.value)){showError(email,'email-error','Please enter a valid email address');isValid=false;}if(phone.value.trim()&&!isValidPhone(phone.value)){showError(phone,'phone-error','Please enter a valid phone number');isValid=false;}if(!subject.value){showError(subject,'subject-error','Please select a subject');isValid=false;}if(!message.value.trim()){showError(message,'message-error','Please enter your message');isValid=false;}else if(message.value.trim().length<10){showError(message,'message-error','Message must be at least 10 characters');isValid=false;}if(isValid){simulateFormSubmission();}}function validateField(field){let isValid=true;const fieldId=field.id;const errorId=`${fieldId}-error`;field.classList.remove('is-invalid');const errorElement=document.getElementById(errorId);if(errorElement){errorElement.style.display='none';}switch(fieldId){case 'name':if(!field.value.trim()){showError(field,errorId,'Please enter your name');isValid=false;}else if(field.value.trim().length<2){showError(field,errorId,'Name must be at least 2 characters');isValid=false;}break;case 'email':if(!field.value.trim()){showError(field,errorId,'Please enter your email');isValid=false;}else if(!isValidEmail(field.value)){showError(field,errorId,'Please enter a valid email address');isValid=false;}break;case 'phone':if(field.value.trim()&&!isValidPhone(field.value)){showError(field,errorId,'Please enter a valid phone number');isValid=false;}break;case 'subject':if(!field.value){showError(field,errorId,'Please select a subject');isValid=false;}break;case 'message':if(!field.value.trim()){showError(field,errorId,'Please enter your message');isValid=false;}else if(field.value.trim().length<10){showError(field,errorId,'Message must be at least 10 characters');isValid=false;}break;}return isValid;}function showError(field,errorId,message){const errorElement=document.getElementById(errorId);if(errorElement){errorElement.textContent=message;errorElement.style.display='block';field.classList.add('is-invalid');field.setAttribute('aria-invalid','true');field.classList.add('shake');setTimeout(()=>{field.classList.remove('shake');},500);}}function resetFormErrors(){const errorMessages=document.querySelectorAll('.error-message');errorMessages.forEach(message=>{message.textContent='';message.style.display='none';});const formFields=contactForm.querySelectorAll('input,select,textarea');formFields.forEach(field=>{field.classList.remove('is-invalid');field.setAttribute('aria-invalid','false');});const formStatus=document.getElementById('form-status');if(formStatus){formStatus.textContent='';formStatus.className='form-status';formStatus.style.display='none';}}function isValidEmail(email){const emailRegex=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;return emailRegex.test(email);}function isValidPhone(phone){const phoneRegex=/^[\d\s()+-.]{7,20}$/;return phoneRegex.test(phone);}function simulateFormSubmission(){const submitButton=contactForm.querySelector('button[type="submit"]');const originalText=submitButton.textContent;submitButton.textContent='Sending...';submitButton.disabled=true;submitButton.classList.add('loading');setTimeout(()=>{const formStatus=document.getElementById('form-status');formStatus.textContent='Thank you for your message!We will get back to you soon.';formStatus.className='form-status success';formStatus.style.display='block';contactForm.reset();submitButton.textContent=originalText;submitButton.disabled=false;submitButton.classList.remove('loading');formStatus.scrollIntoView({behavior:'smooth',block:'nearest'});setTimeout(()=>{formStatus.style.opacity='0';setTimeout(()=>{formStatus.style.display='none';formStatus.style.opacity='1';},500);},5000);},1500);}contactForm.addEventListener('keypress',function(event){if(event.key==='Enter'&&event.target.tagName!=='TEXTAREA'){event.preventDefault();validateForm(event);}});});